import { useState } from "react";
import "./Styles.css";
import Input from "./Input";
import Button from "./Button";
import Label from "./Itemlist";

function App() {


  const [text, setText] = useState("");
  const [value, setValue] = useState()
  const handleChange = (event) => setText(event.target.value);
  const handleClick = unit => setValue(text)




  return (
    <section>
      <div className="box">
        <Input text={text} change={handleChange} />

      </div>
      <div className="button-add">
        <Button onClick={() => handleClick()} text="ADD" />
      </div>
      <div className="button-clean">
        <Button onClick={() => setText("")} text="CLEAN" />
      </div>
      <div className="itemlist">
        <input value={text} onChange={handleClick} />
        <Label text={text} />
        <Button onClick={() => handleClick()} text="X" />
      </div>

    </section>
  );
}

export default App;
