import { useState } from "react";
import "./App.css";
import Input from "./Input";
import Button from "./Button";
import Label from "./Itemlist";

function App() {
  const [text, setText] = useState("");
  const [value, setValue] = useState()

  const handleChange = (event) => setText(event.target.value);
  const handleClick = unit => setValue(value)

  console.log(text);

  return (
    <section>
      <div className="box">
        <Input value={text} action={handleChange} />
      </div>
      <div className="button">
        <Button onClick={() => handleClick()} text="ADD" />
      </div>
      <div className="button">
        <Button onClick={() => handleClick()} text="CLEAN" />
      </div>
      <div className="itemlist">
        <Label text={text} />
        <Button onClick={() => handleClick()} text="X" />
      </div>

    </section>
  );
}

export default App;
